import { connect, useDispatch } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';

function Location(props) {

  let dispatch = useDispatch();

  const fullAddress = ['Street', 'Apartment', 'City', 'State', 'Zipcode'];


  const sendInfo = (values, path) => {
    switch (path) {
      case 'forward':
        dispatch({
          type: 'UPDATE_CUSTOMER',
          payload: values
        });
        dispatch({
          type: 'QUOTE_PROGRESS',
          payload: { divisor: 7, step_number: 4 }
        });
        props.history.push('./driveway');
        break;
      case 'back':
        dispatch({
          type: 'QUOTE_PROGRESS',
          payload: { divisor: 7, step_number: 2 }
        });
        props.history.push('./sub-service');
        break;
      default:
    }
  };

  return (
    <div className='quote'>

      <h2>What's your address?</h2>
      <p>This info helps us verify the address is in our servisable area.</p>

      <Formik
        initialValues={{ street: '', apartment: '', city: '', state: '', zipcode: '' }}
        validate={values => {
          let errors = {};
          if (!values.street || !values.apartment || !values.city || !values.state || !values.zipcode) {
            errors = 'Required';
          }
          return errors;
        }}
        onSubmit={(values, { setSubmitting }) => {
          setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            sendInfo(values, 'forward');
            setSubmitting(false);
          }, 400);
        }}
      >
        {({ isSubmitting }) => (
          <Form>
            {fullAddress.map(a => (
              <>
                <Field type={a} placeholder={a} name={a} />
                <ErrorMessage name={a} component="div" />
              </>
            ))}
            <button onClick={() => sendInfo('', 'back')}>Go sub service</button>
            <button type="submit" disabled={isSubmitting}>
              Go to driveway
            </button>
          </Form>
        )}
      </Formik>

    </div>
  );
}

export default connect()(withRouter(Location));
