import { connect, useDispatch } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { useFormik } from 'formik';
import mapStoreToProps from '../../../redux/mapStoreToProps';
import NextButton from '../../CustomComponents/NextButton';


function Name(props) {

  let dispatch = useDispatch();


  const sendInfo = (values) => {
    console.log('here are my values');

    let step_number = props.store.quoteProgress.step_number;
    dispatch({
      type: 'UPDATE_CUSTOMER',
      payload: values
    });
    dispatch({
      type: 'QUOTE_PROGRESS',
      payload: { divisor: 8, step_number: step_number + 1 }
    });
    props.history.push('./location');
  };

  const validate = values => {
    const errors = {};
    if (!values.name) {
      errors.firstName = 'Required';
    } else if (values.name.length > 15) {
      errors.firstName = 'Must be 15 characters or less';
    }

    return errors;
  };

  const formik = useFormik({
    initialValues: {
      name: props.store.customerInfo.name === '' ? '' : props.store.customerInfo.name,
    },
    validate,
    onSubmit: (values, { setSubmitting }) => {
      alert(JSON.stringify(values, null, 2));
      sendInfo(values);
    },
  });

  return (
    <div className='quoteStep_container'>

      <h2 className='quoteStep_container_title'>What is your first name?</h2>
      <p className='quoteStep_container_title'>We will use this for a more personalized experience throughout this quote.</p>

      <form onSubmit={formik.handleSubmit}>
        <label htmlFor="name">First Name</label>
        <input
          id="name"
          name="name"
          type="text"
          onChange={formik.handleChange}
          value={formik.values.name}
        />
        {formik.errors.name ? <div>{formik.errors.name}</div> : null}

        <div className='quoteStep_nextBtn_container'>
          <NextButton />
        </div>
      </form>
    </div >
  );
}

export default connect(mapStoreToProps)(withRouter(Name));
