import { connect, useDispatch } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage, useFormik } from 'formik';
import mapStoreToProps from '../../../redux/mapStoreToProps';
import NextButton from '../../CustomComponents/NextButton';

function Location(props) {

  let dispatch = useDispatch();

  const sendInfo = (values) => {
    let step_number = props.store.quoteProgress.step_number;
    dispatch({
      type: 'UPDATE_CUSTOMER',
      payload: values
    });
    dispatch({
      type: 'QUOTE_PROGRESS',
      payload: { divisor: 8, step_number: step_number + 1 }
    });
    props.history.push('./driveway');
  };

  const validate = values => {
    const errors = {};

    if (!values.street) {
      errors.street = 'Required';
    }

    if (!values.city) {
      errors.city = 'Required';
    }

    if (!values.state) {
      errors.state = 'Required';
    }

    if (!values.zipcode) {
      errors.zipcode = 'Required';
    }
    return errors;
  };

  let data = props.store.customerInfo;

  const formik = useFormik({
    initialValues: {
      street: data.street === '' ? '' : data.street,
      apartment: data.apartment === '' ? '' : data.apartment,
      city: data.city === '' ? '' : data.city,
      state: data.state === '' ? '' : data.state,
      zipcode: data.zipcode === '' ? '' : data.zipcode,
    },
    validate,
    onSubmit: (values) => {
      sendInfo(values);
    },
  });

  return (
    <div className="quoteStep_container">

      <h2 className='quoteStep_container_title'>What's your address?</h2>
      <p className='quoteStep_container_title'>This info helps us verify the address is in our servicable area.</p>

      <Form>
        <Field type="text" name="street" />
        <ErrorMessage name="street" component="div" />
        <Field type="text" name="apartment" />
        <ErrorMessage name="apartment" component="div" />
        <Field type="text" name="city" />
        <ErrorMessage name="city" component="div" />
        <Field type="text" name="state" />
        <ErrorMessage name="state" component="div" />
        <Field type="text" name="zipcode" />
        <ErrorMessage name="zipcode" component="div" />

        <div className='quoteStep_nextBtn_container'>
          <NextButton disabled={isSubmitting} />
        </div>
      </Form>
      <div className='quoteStep_textFormContainer'>
        <form onSubmit={formik.handleSubmit}>
          {/* Street */}
          <div className='quoteStep_textContainer'>
            <label className='quoteStep_label' htmlFor="name">Street</label>
            <input
              className='quoteStep_text'
              id="street"
              name="street"
              type="text"
              onChange={formik.handleChange}
              value={formik.values.street}
            />
            {formik.errors.street ? <div>{formik.errors.street}</div> : null}
          </div>
          {/* Apartment */}
          <div className='quoteStep_textContainer'>
            <label className='quoteStep_label' htmlFor="name">Apartment</label>
            <input
              className='quoteStep_text'
              id="name"
              name="name"
              type="text"
              onChange={formik.handleChange}
              value={formik.values.name}
            />
            {formik.errors.name ? <div>{formik.errors.name}</div> : null}
          </div>
          {/* City */}
          <div className='quoteStep_textContainer'>
            <label className='quoteStep_label' htmlFor="name">First Name</label>
            <input
              className='quoteStep_text'
              id="name"
              name="name"
              type="text"
              onChange={formik.handleChange}
              value={formik.values.name}
            />
            {formik.errors.name ? <div>{formik.errors.name}</div> : null}
          </div>
          {/* State */}
          <div className='quoteStep_textContainer'>
            <label className='quoteStep_label' htmlFor="name">First Name</label>
            <input
              className='quoteStep_text'
              id="name"
              name="name"
              type="text"
              onChange={formik.handleChange}
              value={formik.values.name}
            />
            {formik.errors.name ? <div>{formik.errors.name}</div> : null}
          </div>
          {/* Zipcode */}
          <div className='quoteStep_textContainer'>
            <label className='quoteStep_label' htmlFor="name">First Name</label>
            <input
              className='quoteStep_text'
              id="name"
              name="name"
              type="text"
              onChange={formik.handleChange}
              value={formik.values.name}
            />
            {formik.errors.name ? <div>{formik.errors.name}</div> : null}
          </div>
          <div className='quoteStep_nextBtn_container'>
            <NextButton />
          </div>
        </form>
      </div>
    </div>
  );
}

export default connect(mapStoreToProps)(withRouter(Location));
