import { connect, useDispatch } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import mapStoreToProps from '../../../redux/mapStoreToProps';
import { useEffect, useState } from 'react';

function Driveway(props) {

  let dispatch = useDispatch();

  const subServices = ['Residential', 'Commercial'];
  const [isChecked, setIsChecked] = useState('');

  useEffect(() => {
    setIsChecked(props.store.customerInfo.subServices);
  }, [props.store.customerInfo.subServices]);

  const sendInfo = (values, path) => {
    switch (path) {
      case 'forward':
        dispatch({
          type: 'UPDATE_CUSTOMER',
          payload: values
        });
        dispatch({
          type: 'QUOTE_PROGRESS',
          payload: { divisor: 7, step_number: 3 }
        });
        props.history.push('./location');
        break;
      case 'back':
        dispatch({
          type: 'QUOTE_PROGRESS',
          payload: { divisor: 7, step_number: 1 }
        });
        props.history.push('./type-of-service');
        break;
      default:
    }
  };

  const goForward = () => {
    dispatch({
      type: 'QUOTE_PROGRESS',
      payload: { divisor: 7, step_number: 5 }
    });
    props.history.push('./sidewalk');
  }

  const goBack = () => {
    dispatch({
      type: 'QUOTE_PROGRESS',
      payload: { divisor: 7, step_number: 3 }
    });
    props.history.push('./location');
  }

  return (
    <div className='quote'>

      <h2>What type of driveway do you have?</h2>

      <input type="radio" id="single_car" name="driveway" value="Single Car" />
      <label htmlFor="single_car">Single Car</label>
      <input type="radio" id="double_car" name="driveway" value="Double Car" />
      <label htmlFor="double_car">Double Car</label>
      <input type="radio" id="triple_car" name="driveway" value="Triple Car" />
      <label htmlFor="triple_car">Triple Car</label>
      <input type="radio" id="different_driveway" name="driveway" value="My driveway is different" />
      <label htmlFor="different_driveway">My driveway is different</label>

      <Formik
        initialValues={{ subServices: '' }}
        validate={values => {
          const errors = {};

          if (props.store.customerInfo.subServices) {
            return errors;
          }
          if (!values.subServices) {
            errors.subServices = 'Please pick an option';
          }
          return errors;
        }}
        onSubmit={(values, { setSubmitting }) => {
          setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            if (props.store.customerInfo.subServices) {
              sendInfo({ service: props.store.customerInfo.subServices }, 'forward');
            } else {
              sendInfo(values, 'forward');
            }
            setSubmitting(false);
          }, 400);
        }}
      >
        {({ isSubmitting }) => (
          <Form>
            <div role="group" aria-labelledby="my-radio-group">
              {subServices.map(s => (
                <label key={s}>
                  <Field
                    type="radio"
                    name="subServices"
                    value={s}
                    checked={isChecked === s}
                    onClick={e => setIsChecked(e.target.value)}
                  />
                  {s}
                </label>
              ))}
            </div>
            <ErrorMessage name="subServices" component="div" />
            <button onClick={() => sendInfo('', 'back')}>Go to type of location</button>
            <button type="submit" disabled={isSubmitting}>Go to services</button>
          </Form>
        )}
      </Formik>
      <button onClick={goBack}>Go to location</button>
      <button onClick={goForward}>Go to sidewalk</button>

    </div>
  );
}

export default connect(mapStoreToProps)(withRouter(Driveway));
