import { connect, useDispatch } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';


function Location(props) {

  let dispatch = useDispatch();

  const goForward = () => {
    dispatch({
      type: 'QUOTE_PROGRESS',
      payload: { divisor: 7, step_number: 4 }
    });
    props.history.push('./driveway');
  }

  const goBack = () => {
    dispatch({
      type: 'QUOTE_PROGRESS',
      payload: { divisor: 7, step_number: 2 }
    });
    props.history.push('./sub-service');
  }

  return (
    <div className='quote'>

      <h2>What's your address?</h2>
      <p>This info helps us verify the address is in our servisable area.</p>

      <Formik
        initialValues={{ email: '', name: '' }}
        validate={values => {
          const errors = {};
          if (!values.email) {
            errors.email = 'Required';
          } else if (
            !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
          ) {
            errors.email = 'Invalid email address';
          }
          return errors;
        }}
        onSubmit={(values, { setSubmitting }) => {
          setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            sendInfo(values, 'forward');
            setSubmitting(false);
          }, 400);
        }}
      >
        {({ isSubmitting }) => (
          <Form>
            <Field type="email" name="email" />
            <ErrorMessage name="email" component="div" />
            <Field type="name" name="name" required />
            <ErrorMessage name="name" component="div" />
            <button onClick={() => sendInfo('', 'back')}>Go home</button>
            <button type="submit" disabled={isSubmitting}>
              Go to driveway
            </button>
          </Form>
        )}
      </Formik>

      <button onClick={goBack}>Go to sub service</button>
      <button onClick={goForward}>Go to driveway</button>

    </div>
  );
}

export default connect()(withRouter(Location));
