import { connect, useDispatch } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';

function Name(props) {

  let dispatch = useDispatch();

  const sendInfo = (values, path) => {
    switch (path) {
      case 'forward':
        dispatch({
          type: 'UPDATE_CUSTOMER',
          payload: values
        });
        dispatch({
          type: 'QUOTE_PROGRESS',
          payload: { divisor: 8, step_number: 1 }
        });
        props.history.push('./type-of-service');
        break;
      case 'back':
        dispatch({
          type: 'QUOTE_PROGRESS',
          payload: { divisor: 8, step_number: 0 }
        });
        props.history.push('/');
        break;
      default:
    }
  };

  const goBack = () => {
    console.log('hello');
  }

  return (
    <div>

      <h2>We are happy you are interested in getting a quote!</h2>
      <p>So that we know what to call you what is your name?</p>

      <Formik
        initialValues={{ name: '' }}
        onSubmit={(values, { setSubmitting }) => {
          setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            sendInfo(values, 'forward');
            setSubmitting(false);
          }, 400);
        }}
      >
        {({ isSubmitting }) => (
          <Form>
            <Field type="name" name="name" required />
            <ErrorMessage name="name" component="div" />
            <div className='quote_backBtnPos'>
              <BackButton onClick={goBack()} />
            </div>
            <button className='btn primary_btn' type="submit" disabled={isSubmitting}>
              Go to type of service
            </button>
          </Form>
        )}
      </Formik>
    </div>
  );
}

export default connect()(withRouter(Name));
