import { takeLatest } from "redux-saga/effects";
import axios from "axios";

function* fetchCustomerSaga() {

  yield axios({
    method: 'GET',
    url: '/api/customer',
  });
  // yield put({
  //   type: "SET_CUSTOMER",
  //   payload: response.data,
  // });
}

function* addCustomerSaga(action) {
  yield axios({
    method: 'POST',
    url: 'https://icecontractormn.herokuapp.com/api/customer',
    data: action.payload
  });
}

