import { connect, useDispatch } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import mapStoreToProps from '../../../redux/mapStoreToProps';
import { useEffect, useState } from 'react';

function subTypes(props) {

  let dispatch = useDispatch();

  const subTypes = ['Residential', 'Commercial'];
  const [isChecked, setIsChecked] = useState('');

  useEffect(() => {
    setIsChecked(props.store.customerInfo.subTypes);
  }, [props.store.customerInfo.subTypes]);

  const sendInfo = (values, path) => {
    switch (path) {
      case 'forward':
        dispatch({
          type: 'UPDATE_CUSTOMER',
          payload: values
        });
        dispatch({
          type: 'QUOTE_PROGRESS',
          payload: { divisor: 7, step_number: 3 }
        });
        props.history.push('./location');
        break;
      case 'back':
        dispatch({
          type: 'QUOTE_PROGRESS',
          payload: { divisor: 7, step_number: 1 }
        });
        props.history.push('./type-of-service');
        break;
      default:
    }
  };

  return (
    <div className='quote'>

      <h2>Is this for residential or commercial?</h2>

      <input type="radio" id="residential" name="sub_service" value="Residential" />
      <label htmlFor="residential">Residential</label>
      <input type="radio" id="commercial" name="sub_service" value="Commercial" />
      <label htmlFor="commercial">Commercial</label>

      <Formik
        initialValues={{ subTypes: '' }}
        validate={values => {
          const errors = {};

          if (props.store.customerInfo.subTypes) {
            return errors;
          }
          if (!values.subTypes) {
            errors.subTypes = 'Please pick an option';
          }
          return errors;
        }}
        onSubmit={(values, { setSubmitting }) => {
          setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            if (props.store.customerInfo.subTypes) {
              sendInfo({ service: props.store.customerInfo.subTypes }, 'forward');
            } else {
              sendInfo(values, 'forward');
            }
            setSubmitting(false);
          }, 400);
        }}
      >
        {({ isSubmitting }) => (
          <Form>
            <div role="group" aria-labelledby="my-radio-group">
              {subTypes.map(s => (
                <label key={s}>
                  <Field
                    type="radio"
                    name="subTypes"
                    value={s}
                    checked={isChecked === s}
                    onClick={e => setIsChecked(e.target.value)}
                  />
                  {s}
                </label>
              ))}
            </div>
            <ErrorMessage name="subTypes" component="div" />
            <button onClick={() => sendInfo('', 'back')}>Go to type of location</button>
            <button type="submit" disabled={isSubmitting}>Go to services</button>
          </Form>
        )}
      </Formik>

      <button onClick={goBack}>Go to type of service</button>
      <button onClick={goForward}>Go to location</button>

      <p>Hi, {props.store.customerInfo.firstName} {props.store.customerInfo.lastName}!</p>


    </div>
  );
}

export default connect(mapStoreToProps)(withRouter(subTypes));
